    domain: "foobar.example",
    soaEmail: "example@foobar.example",
    tags: [
        "foo",
        "bar",
    ],
});
const foobarDomainRecord = new linode.DomainRecord("foobar", {
    domainId: foobar.id,
    name: "www",
    recordType: "CNAME",
    target: "foobar.example",
});
