import * as pulumi from "@pulumi/pulumi";
import * as linode from "@pulumi/linode";

const foo = new linode.Token("foo", {
    label: "token",
    scopes: "linodes:read_only",
    expiry: "2100-01-02T03:04:05Z",
});
const fooInstance = new linode.Instance("foo", {});
```
